$("#player-request").on("click",function(){$.ajax({type:"POST",url:"/StellarView/GetUser",data:{player:$("#user-name").val(),server:$("#server-name").val()},dataType:"json",success:function(n){$("#query-result").empty();n.status==="success"?$.each(n.data,function(n,t){$("#query-result").append(t.coords+" - "+t.name+"<br />")}):$("#query-result").html(n.data)}})});